import React, { Component } from 'react'
import axios from 'axios'
import fb from './../config/Firebase'

class PlayPage extends Component {
  constructor(props){
    super(props)
    this.state = {
      user : props.user,
      room : ""
    }

    setInterval(()=>{
      axios.get(`http://localhost:3000/game_rooms/${props.match.params.id}.json`)
      .then((result) => {
        this.setState({
          room : result.data
        })
        // console.log(this.state.room)
      })
    },1000)

  }


  joinRoom (e) {
    console.log(e.target.value)
  }


  render() {
    return (
      <div>
        <p>{this.props.match.params.id}</p>
        <p onClick={this.joinRoom} value={this.props.user.uid}>{this.props.user.uid}</p>
      </div>
    )
  }
}

export default PlayPage
