import React, { Component } from 'react'
import fb from './../config/Firebase'

class GameRoom extends Component {


  componentWillMount () {
    const full = true
    const roomDB = fb.database().ref().child("game_rooms").child(this.props.room.id)
    roomDB.on('value', snap => {
      const players = snap.val().user_list
      if (players[0] === 0 || players[1] === 0) {
        full = false
      }
    })
  }

  render () {
    return (
      <div>
        <h3>Room {this.props.room.id}</h3>
        <p>Player 1: {this.props.room.user_list[0]}</p>
        <p>Player 2: {this.props.room.user_list[1]}</p>
        {(!this.full) ? <button><a href={`#/session/${this.props.room.id}`}>Join</a></button> : ""}
      </div>
    )
  }
}

export default GameRoom
