import React, { Component } from 'react'
import fb from './../config/Firebase'

import Tictactoe from './tictactoe/Tictactoe'

class PlayPage extends Component {
  constructor(props){
    super(props)
    this.state = {
      user : this.props.user,
      room : ""
    }

    setInterval(this.fetchGameRoomInfo,1000)

  }


  fetchGameRoomInfo (props) {
    const game_roomsDB = fb.database().ref().child("game_rooms").child(props.match.params.id)
    game_roomsDB.on('value', snap => {
      this.setState({ room : snap.val()})
    })
  }


  render() {
    return (
      <div>
        <p>Game Room No: {this.props.match.params.id}</p>
        <p>Player 1: {this.state.room.user1}</p>
        <p>Player 2: {this.state.room.user2}</p>
        <button>Start</button>
        <button>Leave</button>
        // <Tictactoe room={this.state.room}/>
      </div>
    )
  }
}

export default PlayPage
