import React, { Component } from 'react';
import fb from './../config/Firebase'

import Nav from './Nav';
import GameList from './GameList';
import Footer from './Footer';
import GameRoom from './GameRoom'

const games = {
  tictactoe : {
    name : "Tic Tac Toe",
    board : "[0,0,0,0,0,0,0,0,0]",
    capacity : 2,
  }
}

class GameRoomListPage extends Component {
  constructor(){
    super()
    this.state = {rooms : []}

    this.fetchGameRooms()
  }

  fetchGameRooms(){
    const game_roomsDB = fb.database().ref().child("game_rooms")
    game_roomsDB.on('value', snap => {
      this.setState({ rooms : snap.val()})
    })
  }

  componentDidMount(){

  }

  authListener() {
    fb.auth().onAuthStateChanged(
      (user) => {
        // console.log(user);
        if (user) {
          console.log(user)
          // localStorage.setItem('user', user.uid);
        }
      }
    )
  }

  render() {
    return (
      <div className="App" id="page-top">
        <div className="wrapper">
        <Nav user={this.props.user}/>
          <section className="portfolio" id="portfolio">
            <div className="container">
              <h2 className="text-center text-uppercase text-secondary mb-0">{games[this.props.match.params.name]["name"]}</h2>
                <hr className="star-dark mb-5" />
                <div className="row">
                {this.state.rooms.map((room)=>{
                  if (room.status === "before") {
                    return (<GameRoom key={room.id} room={room} user={this.props.user}/>)
                  }
                })}
                <p>The current User is: {this.props.user.uid}</p>
                </div>
              </div>
          </section>
        </div>
        <div className="pusher">
        </div>
        <Footer />
      </div>
    );
  }
}

export default GameRoomListPage;
