import React, { Component } from 'react'
import axios from 'axios'
import fb from './../config/Firebase'

import Tictactoe from './tictactoe/Tictactoe'

class PlayPage extends Component {
  constructor(props){
    super(props)
    this.state = {
      user : this.props.user,
      room : ""
    }
    setTimeout(()=>{
      this.setState({ user: this.props.user})
      console.log(this.state.user)
      axios.get(`http://localhost:3000/game_rooms/${props.match.params.id}.json`)
      .then((result) => {
        if (result.data.user1 === "") {
          axios.patch(`http://localhost:3000/game_rooms/${props.match.params.id}`, {
            "user1" : this.state.user.email
          })
        } else {
          axios.patch(`http://localhost:3000/game_rooms/${props.match.params.id}`, {
            "user2" : this.state.user.email
          })
        }
        })

    },1000)
    // setInterval(()=>{
    //   axios.get(`http://localhost:3000/game_rooms/${props.match.params.id}.json`)
    //   .then((result) => {
    //     this.setState({
    //       room : result.data
    //     })
    //     if (result.data.user1 !== "" && result.data.user2 !== "") {
    //       axios.patch(`http://localhost:3000/game_rooms/${props.match.params.id}`, {
    //         "status" : "in game"
    //       })
    //     }
    //
    //   })
    // },1000)

  }


  joinRoom (e) {
    console.log(e.target.value)
  }


  render() {
    return (
      <div>
        <p>Game Room No: {this.props.match.params.id}</p>
        <p>Player 1: {this.state.room.user1}</p>
        <p>Player 2: {this.state.room.user2}</p>
        <button>Start</button>
        <button>Leave</button>
        <Tictactoe room={this.state.room}/>
      </div>
    )
  }
}

export default PlayPage
