import React, { Component } from 'react'
import axios from 'axios'

class GameRoom extends Component {
  constructor(){
    super()
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick () {
    axios.post(`http://localhost:3000/game_rooms/${this.props.room.id}`, {"user1" : this.props.user.email})
  }

  render () {
    return (
      <div>
        <h3>Room {this.props.room.id}</h3>
        <p>Player 1: {this.props.room.user1}</p>
        <p>Player 2: {this.props.room.user2}</p>
        <button><a href={`#/session/${this.props.room.id}`}>Join</a></button>
      </div>
    )
  }
}

export default GameRoom
