import React, { Component } from 'react'
import axios from 'axios'

class PlayPage extends Component {
  constructor(props){
    super()
    this.state = {
      room : ""
    }



    setInterval(()=>{
      axios.get(`http://localhost:3000/game_rooms/${props.match.params.id}.json`)
      .then((result) => {
        this.setState({
          room : result.data
        })
        console.log(this.state.room)
      })
    },1000)

  }

  componentDidMount () {
    axios.post(`http://localhost:3000/game_rooms/${props.match.params.id}`, {
      "user_list" : `${this.state.room.user_list}, ${this.props.user.uid}`
    })
  }


  render() {
    return (
      <div>
        <p>{this.props.match.params.id}</p>
      </div>
    )
  }
}

export default PlayPage
