import React, { Component } from 'react'
import axios from 'axios'

class PlayPage extends Component {
  constructor(props){
    super(props)
    this.state = {
      user : props.user,
      room : ""
    }

    setInterval(()=>{
      axios.get(`http://localhost:3000/game_rooms/${props.match.params.id}.json`)
      .then((result) => {
        this.setState({
          room : result.data
        })
        console.log(this.state.room)
      })
    },1000)

  }



  render() {
    return (
      <div>
        <p>{this.props.match.params.id}</p>
        {axios.patch(`http://localhost:3000/game_rooms/${this.props.match.params.id}`, {"user_list" : 2})}
      </div>
    )
  }
}

export default PlayPage
